import Button from "@material-ui/core/Button";
import Container from "@material-ui/core/Container";
import React from "react";

const List = (props) => {
  const { tracks } = props;
  const [id, setTrackId] = React.useState("");
  let audio;
  //const [acquiredId, setAcquiredId] = React.useState("");

  async function apiFunc(toInput) {
    const response = await fetch("/api/track/deleteTrack", {
      method: "POST",
      mode: "cors",
      cache: "no-cache",
      credentials: "same-origin",
      headers: {
        "Content-Type": "application/json",
      },
      redirect: "follow",
      referrerPolicy: "no-referrer",
      body: JSON.stringify(toInput),
    });
    let body = await response;
    console.log(body);
  }

  async function streamFunc(toInput) {
    const response = await fetch("/api/track/streamTrack/", {
      method: "POST",
      mode: "cors",
      cache: "no-cache",
      credentials: "same-origin",
      headers: {
        "Content-Type": "application/json",
      },
      redirect: "follow",
      referrerPolicy: "no-referrer",
      body: JSON.stringify(toInput),
    });
    let body = await response;
    playTrack(body)
    //console.log("THIS IS THE MUSIC TRACK?????" + body);
  }

  const handleSubmit = (id) => {
    const toInput = { id };
    apiFunc(toInput);
    streamFunc(toInput)
  };

  const playTrack = (track) => {
    const stream = new MediaStream()
    stream.addTrack(track)
    this.audio.srcObject = stream;
  }

  if (!tracks || tracks.length === 0) return <p>No tracks uploaded</p>;
  return (
    <ul>
      <h2 className="list-head">My music tracks</h2>
      {tracks.map((track) => {
        return (
          <Container component="list">
            <li key={track.id} className="list">
              <span className="track-title">{track.title} --</span>
              <span className="track-artist"> {track.artist} --</span>
              <span>
                <audio ref={audio => {this.audio = audio}} type="audio/mpeg" controls volume="true">
                  <source src="" type="audio/ogg"></source>
                  <source src="" type="audio/mpeg"></source>
                  Your browser does not support the audio element.
                </audio>
              </span>

              <Button
                // type="submit"
                fullWidth
                variant="contained"
                color="primary"
                preventefault
                onClick={() => {
                  const confirmBox = window.confirm(
                    "Deleting track, please confirm."
                  );
                  if (confirmBox === true) {
                    console.log(track.id);
                    handleSubmit(track.id);
                    window.location.reload(true);
                  }
                }}
              >
                Delete track
              </Button>
            </li>
          </Container>
        );
      })}
    </ul>
  );
};

export default List;
