import Box from "@material-ui/core/Box";
import Container from "@material-ui/core/Container";
import CssBaseline from "@material-ui/core/CssBaseline";
import { makeStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import React from "react";
import list from "./userEventList";
import eventBannerLoading from "./eventBannerLoading";
import { Zoom } from "react-slideshow-image";
import image1 from "../Assests/Images/1.jpeg";
import image2 from "../Assests/Images/2.jpeg";
import image3 from "../Assests/Images/3.jpeg";
const images = [image1, image2, image3];

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(7),
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: "100%", // Fix IE 11 issue.
    marginTop: theme.spacing(3),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: "100%",
  },
}));

const zoomOutProperties = {
  duration: 5000,
  transitionDuration: 350,
  infinite: true,
  indicators: true,
  scale: 0.4,
  arrows: false,
};

const Slideshow = () => {
  return (
    <div className="slide-container">
      <Zoom {...zoomOutProperties}>
        {images.map((each, index) => (
          <img key={index} style={{ width: "100%" }} src={each} />
        ))}
      </Zoom>
    </div>
  );
};

useEffect(() => {
  setAppState({ loading: true });
  const apiUrl = `/api/track/getAllFlyers`;
  fetch(apiUrl)
    .then((res) => res.json())
    .then((tracks) => {
      setAppState({ loading: false, tracks: tracks });
    });
}, [setAppState]);

// const EventBanner = () => {
