import React, { useState, useEffect } from "react";
import { Collapse } from "reactstrap";

function CollapsibleContainer({ children, ...props }) {
  const { title, collapse } = props;
  const [isCollapsed, setIsCollapse] = useState(collapse);
  const [icon, setIcon] = useState("fa fa-chevron-down");
  const toggle = () => {
    setIsCollapse(!isCollapsed);
    setIcon(state => {
      return state === "fa fa-chevron-down"
        ? "fa fa-chevron-right"
        : "fa fa-chevron-down";
    });
  };

  const animate = collapse => {
    setIsCollapse(collapse);
    setIcon(state => {
      return state === "fa fa-chevron-down"
        ? "fa fa-chevron-right"
        : "fa fa-chevron-down";
    });
  };

  useEffect(() => {
    animate(!collapse);
  }, [collapse]);

  return (
    <div className="coll-panel">
      <button
        type="button"
        className="coll-panel-btn btn-primary btn-block text-left"
        onClick={() => toggle()}
      >
        <i className={icon} /> {title}
      </button>
      <Collapse className="border text-left p-2" isOpen={isCollapsed}>
        {children}
      </Collapse>
    </div>
  );
}

CollapsibleContainer.defaultProps = {
  children: "Sample children",
  title: "Title",
  collapse: true
};

export default CollapsibleContainer;
