import Container from "@material-ui/core/Container";
import React from "react";
import { makeStyles } from "@material-ui/core/styles";


const useStyles = makeStyles((theme) => ({
  root: {
    [theme.breakpoints.up('Xs')]: {
      maxWidth: '100%',
    },
    [theme.breakpoints.up('Lg')]: {
      maxWidth: '100%',
    },
  },
  rootDiv: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'space-around',
    overflow: 'hidden',
    backgroundColor: theme.palette.background.paper,
  },
  trackBox: {
    width: '100%',
    height: 150,
  },
  indivTrack: {

  },
}));

const UserTrackList = (props) => {
  const classes = useStyles();
  const { tracks } = props;

  const setTrackSource = (id) => {
    return `http://localhost:8080/api/track/streamTrack/${id}`
  }

  if (!tracks || tracks.length === 0) return <p>No tracks at the moment, check back later!</p>;
  return (
    <Container className={classes.rootDiv}>
      <h2 >InDMix Tracks</h2>
      {tracks.map((track) => {
        return (
          <Container component="div" className={classes.trackBox}>
            <div key={track.id} className={classes.indivTrack}>
              <span className="track-id">{track.id} --</span>
              <span className="track-title">{track.title} --</span>
              <span className="track-artist"> {track.artist} --</span>
              <span>
                <audio type="audio/mpeg" controls volume="true">
                  <source src={setTrackSource(track.id)} type="audio/mpeg"></source>
                  Your browser does not support the audio element.
                </audio>
              </span>
            </div>
          </Container>
        );
      })}
    </Container>
  );
};

export default UserTrackList;
