export const getOffset = (el: HTMLElement | null): number => {
  const rect: DOMRect | undefined = el?.getBoundingClientRect(),
    scrollTop = window.pageYOffset || document.documentElement.scrollTop;
  return rect!.top + scrollTop;
};
import React from "react";
import { getOffset } from "../../util";
import { HideBetweenProps, HideBetweenState } from "../../Interfaces";

class HideBetween extends React.Component<HideBetweenProps, HideBetweenState> {
  state = {
    show: false,
  };

  componentDidMount() {
    window.addEventListener("scroll", this.listenToScroll);
  }

  componentWillUnmount() {
    window.removeEventListener("scroll", this.listenToScroll);
  }

  listenToScroll = () => {
    const {
      startDivID,
      endDivID,
      startHeight,
      endHeight,
      height,
      inverse,
      startDivOffset,
      endDivOffset,
    } = this.props;

    let startDiv: HTMLElement | null = null,
      endDiv: HTMLElement | null = null;

    if (!height) {
      startDiv = document.querySelector(`#${startDivID}`) as HTMLElement;
      endDiv = document.querySelector(`#${endDivID}`) as HTMLElement;
    }

    let startDivTopOffset: number = height
      ? startHeight || 0
      : getOffset(startDiv);

    let endDivTopOffset: number = height ? endHeight || 0 : getOffset(endDiv);

    if (!height) {
      if (startDivOffset) startDivTopOffset += startDivOffset;
      else if (endDivOffset) endDivTopOffset += endDivOffset;
    }

    const winScroll: number =
      document.body.scrollTop || document.documentElement.scrollTop;

    if (winScroll >= startDivTopOffset && winScroll <= endDivTopOffset) {
      this.setState({
        show: inverse,
      });
    } else {
      this.setState({
        show: !inverse,
      });
    }
  };

  render() {
    return (
      <React.Fragment>{this.state.show && this.props.children}</React.Fragment>
    );
  }
}

export default HideBetween;
import React from "react";
import { getOffset } from "../../util";
import { HideOnProps, HideOnState } from "../../Interfaces";

class HideOn extends React.Component<HideOnProps, HideOnState> {
  public static defaultProps = {
    showOnPageInit: true,
  };

  state = {
    show: false,
  };

  componentDidMount() {
    this.props.showOnPageInit && this.listenToScroll();
    window.addEventListener("scroll", this.listenToScroll);
  }

  componentWillUnmount() {
    window.removeEventListener("scroll", this.listenToScroll);
  }

  listenToScroll = () => {
    const { atHeight, inverse, divID, offset, height } = this.props;

    let div: HTMLElement | null = null;

    if (!atHeight) {
      div = document.querySelector(`#${divID}`) as HTMLElement;
    }

    let divTopOffset: number = height ? height || 0 : getOffset(div);

    if (!height && offset) {
      divTopOffset += offset;
    }

    const winScroll: number =
      document.body.scrollTop || document.documentElement.scrollTop;

    if (winScroll >= divTopOffset) {
      this.setState({
        show: inverse,
      });
    } else {
      this.setState({
        show: !inverse,
      });
    }
  };

  render() {
    return (
      <React.Fragment>{this.state.show && this.props.children}</React.Fragment>
    );
  }
}

export default HideOn;
import React from "react";
import { HideProps, HideState } from "../../Interfaces";

class HideScroll extends React.Component<HideProps, HideState> {
  public static defaultProps = {
    variant: "down",
    showOnPageInit: true
  };

  state = {
    show: HideScroll.defaultProps.showOnPageInit && this.props.showOnPageInit,
    prevYOffset: window.pageYOffset,
  };

  componentDidMount() {
    window.addEventListener("scroll", this.listenToScroll);
  }

  componentWillUnmount() {
    window.removeEventListener("scroll", this.listenToScroll);
  }

  listenToScroll = () => {
    const { prevYOffset } = this.state;
    const { variant } = this.props;

    const currentYOffset = window.pageYOffset;

    if (prevYOffset > currentYOffset) {
      this.setState({
        show: variant === "down",
        prevYOffset: currentYOffset,
      });
    } else {
      this.setState({
        show: variant !== "down",
        prevYOffset: currentYOffset,
      });
    }
  };

  render() {
    return (
      <React.Fragment>{this.state.show && this.props.children}</React.Fragment>
    );
  }
}

export default HideScroll;
import React from "react";
import { HideDuringProps, HideDuringState } from "../../Interfaces";

class HideDuring extends React.Component<HideDuringProps, HideDuringState> {
  timeout: NodeJS.Timeout | null = null;

  state = {
    show: false,
  };

  componentDidMount() {
    window.addEventListener("scroll", this.listenToScroll);
  }

  componentWillUnmount() {
    window.removeEventListener("scroll", this.listenToScroll);
  }

  listenToScroll = () => {
    this.setState({
      show: !this.props.inverse,
    });

    this.timeout && clearTimeout(this.timeout);

    this.timeout = setTimeout(() => {
      this.setState({ show: this.props.inverse ? true : false });
    }, 100);
  };

  render() {
    return (
      <React.Fragment>{this.state.show && this.props.children}</React.Fragment>
    );
  }
}

export default HideDuring;
